import * as api from '../api';

//Action Creators
//actions are functions which have type and payload
// we use redux thunck as we are dealing with asynchronous 
// logic we add async dispatch function in front and
// then dispatch the actin

export const getPosts = () => async (dispatch) => {
    try {
        const { data } = await api.fetchPosts();

        dispatch({ type: 'FETCH_ALL', payload: data});
    } 
    catch (error)
    {
        console.log(error.message);
    }
}

export const createPost = () => async (dispatch) => {
    try {
        const {data} = await api.createPost();

        dispatch({type: ''})
    }
