// reducer is a function that accepts the state 
// and accepts the action and according to the 
// type of the action it returns state changed 
// by it

//after takeing data from actions
//and dispatched through app.js
//we immidiatly come here to reduce

import { FETCH_ALL, CREATE, UPDATE, DELETE, LIKE, FETCH_BY_SEARCH, START_LOADING, END_LOADING } from '../constants/actionTypes';

export default (state = [], action) => {
    switch (action.type) {

        case START_LOADING:
            return { ...state, isLoading: true};

        case FETCH_ALL:
            return {
                ...state,
                posts: action.payload.data,
                currentPage: action.payload.currentPage,
                numberOfPages: action.payload.numberOfPages,
            };
        case FETCH_BY_SEARCH:
            return { ...state, posts: action.payload};
        case LIKE:
            return state.map((post) => (post._id === action.payload._id ? action.payload : post));
        case CREATE:
            return [...state, action.payload];
        case UPDATE:
            return state.map((post) => (post._id === action.payload._id ? action.payload : post));
        case DELETE:
            return state.filter((post) => post._id !== action.payload);
        default:
            return state;
    }
}
